import { Task } from './../shared/task';
import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-task-list-item',
  templateUrl: './task-list-item.component.html',
  styleUrls: ['./task-list-item.component.css']
})
export class TaskListItemComponent implements OnInit {

  @Input()
  private _task: Task | undefined;
  public get task(): Task | undefined {
    return this._task;
  }
  public set task(value: Task | undefined) {
    this._task = value;
  }

  constructor() {}

  ngOnInit(): void {

  }

}
<div class="form-group form-check">
  <input type="checkbox" class="form-check-input" />
</div>
<div [innerHTML]="task.description" class="task-item-description"
  ngClass="task.completed? 'task-completed' : ''">
</div>
<div type="button" [routerLink]="['/edit', task.id]" class="btn btn-primary mr-1">
  Alterar
</div>
<div>
  <button type="button" class="btn btn-danger">
    Excluir
  </button>
</div>
